<?php 
    header("Content-type: text/plain; charset=UTF-8");
    function hexToStr($hex)
    {
        $string='';
        for ($i=0; $i < strlen($hex)-1; $i+=2)
        {
            $string .= chr(hexdec($hex[$i].$hex[$i+1]));
        }
        return $string;
    }
    $param = "|";
    $decoded = hexToStr(substr($param,1));
    for ($i=0; $i < strlen($decoded); ++$i)
    {
	if(ord($decoded[$i])==124){
            echo $param;
        }else{
            echo $decoded[$i];
        }
    }
?>